
public class Ops {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=2;
		int j=5;
		double r=3.5;
		System.out.println (i+j);// forvent 7
		System.out.println (i-j);//forvent -3
		System.out.println (j-i);//forvent 3
		System.out.println (i*j);//forvent 10
		System.out.println (i/j);
		System.out.println (j/i);
		
		System.out.println (i*r);
		System.out.println (i/r);
		System.out.println (r/j);

	}

}
